INSERT INTO `bookinfo` VALUES (1,' C语言 ','7-5609-1848-4','215238','{\"isbn\": \"7-5609-1848-4\", \"kind\": \"info\", \"name\": [\"ISBN:\", \"语种:\", \"题名:\", \"出版发行:\", \"载体形态:\", \"主题:\", \"中图分类:\", \"主要著者:\", \"索书号\", \"标签:\", \"随书附盘：\", \"相关主题:\"], \"image\": \"https://img3.doubanio.com/f/shire/5522dd1f5b742d1e1394a17f44d590646b63871d/pics/book-default-lpic.gif\", \"value\": [\"7-5609-1848-4 价格： CNY12.50\", \"chi\", \"C语言 / 李刚主编 ,\", \"出版地： 武汉 出版社： 华中理工大学出版社 出版日期： 1998\", \"188页 26cm\", \"C语言 基本知识\", \"TP312 版次： 4\", \"李刚 主编\", \"TP312C/141\", \"没有标签\", \"\", \"集合语言 可扩充语言 指令系统 PL/1语言 LOGO语言 数据流式语言 模拟语言 机器人语言 FORTRAN语言 专用语言 DYNAMO语言 LISP语言 APT语言 符号语言 C语言 图形语言 自编译语言 数据抽象语言 AED语言 BASIC语言 并行程序语言 PROLOG语言 命令语言 算法语言 系统程序设计语言 计算机结构语言 ALGOL语言 汇编语言 作业控制语言 数据描述语言 程序语言 规范语言 人 面向过程语言 ADA语言 COBOL语言 编辑语言 仿真语言 宏汇编语言 相联语言 结构化语言 APL语言 通用语言 报表语言 人工智能语言 过程控制语言 编译程序语言 会话语言 PASCAL语言 微程序设计语言 面向问题语言 更多...\"], \"author\": \" 李刚主编 ,\", \"bookName\": \"C语言, 李刚主编\", \"contentValidity\": \"\", \"AuthorBriefIntroduction\": \"\"}'),(2,' C语言 ','0','75231','{\"isbn\": \"\", \"kind\": \"info\", \"name\": [\"ISBN:\", \"语种:\", \"题名:\", \"版次:\", \"出版发行:\", \"载体形态:\", \"中图分类:\", \"主要著者:\", \"索书号\", \"标签:\", \"随书附盘：\"], \"image\": \"https://img3.doubanio.com/f/shire/5522dd1f5b742d1e1394a17f44d590646b63871d/pics/book-default-lpic.gif\", \"value\": [\"价格： CNY6.20\", \"chi\", \"C语言 / 李智渊编著 ,\", \"第3版\", \"出版地： 成都 出版社： 电子科技大学出版社 出版日期： 1991\", \"245页 26cm\", \"TP312C 版次：\", \"李智渊 编著\", \"TP312C/12\", \"没有标签\", \"\"], \"author\": \" 李智渊编著 ,\", \"bookName\": \"C语言, 李智渊编著\", \"contentValidity\": \"\", \"AuthorBriefIntroduction\": \"\"}'),(3,' C语言 ','0','74949','{\"isbn\": \"\", \"kind\": \"info\", \"name\": [\"ISBN:\", \"语种:\", \"题名:\", \"出版发行:\", \"载体形态:\", \"中图分类:\", \"主要著者:\", \"索书号\", \"标签:\", \"随书附盘：\", \"相关主题:\"], \"image\": \"https://img3.doubanio.com/f/shire/5522dd1f5b742d1e1394a17f44d590646b63871d/pics/book-default-lpic.gif\", \"value\": [\"价格： 2.25元\", \"chi\", \"C语言 / 李智渊编译 ,\", \"出版地： 成都 出版社： 四川科学技术出版社 出版日期： 1985.9\", \"335页 19cm\", \"TP312 版次：\", \"李智渊 编译\", \"TP312/61\", \"没有标签\", \"\", \"集合语言 可扩充语言 指令系统 PL/1语言 LOGO语言 数据流式语言 模拟语言 机器人语言 FORTRAN语言 专用语言 DYNAMO语言 LISP语言 APT语言 符号语言 C语言 图形语言 自编译语言 数据抽象语言 AED语言 BASIC语言 并行程序语言 PROLOG语言 命令语言 算法语言 系统程序设计语言 计算机结构语言 ALGOL语言 汇编语言 作业控制语言 数据描述语言 程序语言 规范语言 人 面向过程语言 ADA语言 COBOL语言 编辑语言 仿真语言 宏汇编语言 相联语言 结构化语言 APL语言 通用语言 报表语言 人工智能语言 过程控制语言 编译程序语言 会话语言 PASCAL语言 微程序设计语言 面向问题语言 更多...\"], \"author\": \" 李智渊编译 ,\", \"bookName\": \"C语言, 李智渊编译\", \"contentValidity\": \"\", \"AuthorBriefIntroduction\": \"\"}'),(4,' Hello C语言 ','978-7-121-31746-0','1900066769','{\"info\": {\"name\": [\"作者\", \"出版社:\", \"出版年:\", \"页数:\", \"定价:\", \"装帧:\", \"ISBN:\"], \"value\": [\"郑赟 / 郑洪清\", \" 电子工业出版社\", \" 2017-7-20\", \"350\", \"59\", \"平装\", \"9787121317460\"]}, \"kind\": \"douban\", \"image\": \"https://img9.doubanio.com/view/subject/l/public/s29504376.jpg\", \"bookName\": \"Hello C语言\", \"contentValidity\": \"《Hello C语言》抛弃了传统的知识与项目分开讲解的方式，直接引入设计好的项目，带领你循序渐进开发出来。书中的案例不再是实验性的小例子，而是各种有趣的游戏。例如女朋友数玫瑰、爱情心理测试、五子棋、全民打飞机、自虐小鸟、连连看、俄罗斯方块、围住神经猫等等。\", \"AuthorBriefIntroduction\": \"郑赟，数学硕士，2000年毕业于大连理工大学，一直从事软件编程。曾在央企、外企工作过，参与过千万级代码量的项目维护与升级。擅长C，C++，java，html5，javascript，python等开发语言。曾主导开发VHDL-AMS的编译器与模拟器、负面舆情平台、在线医疗平台，银行智能排队系统等项目。在17年的编程生涯中，累计代码几十万行。作为技术专家，曾去过中科院研究生院、北京工业大学、北京工商大学，北京对外经济贸易大学等几十所高校做讲座！把编程知识全部融入做包子的过程中，深受学生与老师喜欢！\"}'),(5,' ANSI C C语言入门 ','7-81016-401-5','269476','{\"isbn\": \"7-81016-401-5\", \"kind\": \"info\", \"name\": [\"ISBN:\", \"语种:\", \"题名:\", \"出版发行:\", \"载体形态:\", \"题名自由词:\", \"主题:\", \"中图分类:\", \"科图分类:\", \"主要著者:\", \"索书号\", \"标签:\", \"随书附盘：\"], \"image\": \"https://img3.doubanio.com/f/shire/5522dd1f5b742d1e1394a17f44d590646b63871d/pics/book-default-lpic.gif\", \"value\": [\"7-81016-401-5 价格： ￥5.40\", \"chi\", \"ANSI C C语言入门 / 彭光泽编译 ,\", \"出版地： 成都 出版社： 电子科技大学出版社 出版日期： 1992.6\", \"196页 26cm\", \"ANIC C C语言\", \"C语言 基本知识\", \"TP312C 版次： 3\", \"73.87221 版次：\", \"彭光泽 编译\", \"TP31/423\", \"没有标签\", \"\"], \"author\": \" 彭光泽编译 ,\", \"bookNam