INSERT INTO `newsinfo` VALUES (1,'{\"jiaowu\": {\"jaowunotice\": [{\"url\": \"http://jwc.yangtzeu.edu.cn/http://jwc.yangtzeu.edu.cn/info/1049/6751.htm\", \"title\": \"长江大学2020年上半年全国大学英语四、六级考试公告（1号）\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1083/6786.htm\", \"title\": \"2019-2020学年教学奖励评审结果公示\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1083/6781.htm\", \"title\": \"2019～2020学年“任大龙奖教金”拟获奖教师名单公示\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/http://cxcy.yangtzeu.edu.cn/info/1013/1391.htm\", \"title\": \"关于我校第十三批“大学生创新创业训练计划”项目申报的通知\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1083/6711.htm\", \"title\": \"关于长江大学承训点开展2020年湖北省高校教师岗前培训的通知\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1083/6791.htm\", \"title\": \"关于全国大学英语四、六级考试期间停课的通知\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1083/6783.htm\", \"title\": \"2020-2021学年第一学期开学教学情况通报(三）\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1083/6771.htm\", \"title\": \"开学第二周教学工作提示\"}], \"jaowudynamic\": [{\"url\": \"http://jwc.yangtzeu.edu.cn/info/1086/6742.htm\", \"title\": \"2020-2021学年第一学期开学教学情况通报(二）\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1086/6741.htm\", \"title\": \"2020-2021学年第一学期开学教学情况通报(一）\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1086/6651.htm\", \"title\": \"教务处党支部开展6月主题党日活动\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1086/6586.htm\", \"title\": \"教务处党支部开展5月份主题党日活动\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1086/6561.htm\", \"title\": \"我校受邀在全球大学专题研讨会交流线上教学经验\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1086/6552.htm\", \"title\": \"教务处召开学籍秘书工作会议，重点推进毕业生工作\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1086/6534.htm\", \"title\": \"教务处党支部开展4月份主题党日活动\"}, {\"url\": \"http://jwc.yangtzeu.edu.cn/info/1086/6512.htm\", \"title\": \"长江大学疫情期间在线教学质量报告（1-4周）\"}]}, \"xuexiao\": {\"rollnews\": [{\"url\": \"images/20200908-1.jpg\", \"image\": \"http://www.yangtzeu.edu.cn/images/20200908-1.jpg\"}, {\"url\": \"http://news.yangtzeu.edu.cn/info/1002/25985.htm\", \"image\": \"http://www.yangtzeu.e